import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { loginUser } from '../api';
import '../login.css';
import '../loading.css'; // For the loading spinner

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [loginError, setLoginError] = useState('');
  const [redirecting, setRedirecting] = useState(false);
  const navigate = useNavigate();

  // Check for existing user on mount
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user?.data?.token) {
      setRedirecting(true);
      setTimeout(() => navigate('/home', { replace: true, state:{login:true} }), 1500);
    }
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: ''
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setIsLoading(true);
    setLoginError('');

    try {
      const response = await loginUser(formData);
      localStorage.setItem('user', JSON.stringify(response));
      navigate('/home', { replace: true });
    } catch (error) {
      setLoginError(error.response?.data?.message || 'Login failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const validateForm = () => {
    // ... keep your existing validation logic ...
  };

  if (redirecting) {
    return (
      <div className="loading-overlay">
        <div className="loading-spinner"></div>
        <p>Redirecting to your account...</p>
      </div>
    );
  }
  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <img 
            src={require('../assetss/images/logo.jpg')} 
            alt="Recipe Genie Logo" 
            className="login-logo"
          />
          <h2 className="login-title">Welcome Back</h2>
          <p className="login-subtitle">Sign in to access your recipes</p>
        </div>

        {loginError && <div className="login-error">{loginError}</div>}

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email" className="form-label">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className={`form-input ${errors.email ? 'input-error' : ''}`}
              placeholder="your@email.com"
            />
            {errors.email && <span className="error-message">{errors.email}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="password" className="form-label">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className={`form-input ${errors.password ? 'input-error' : ''}`}
              placeholder="••••••••"
            />
            {errors.password && <span className="error-message">{errors.password}</span>}
          </div>

          <button 
            type="submit" 
            className="login-button"
            disabled={isLoading}
          >
            {isLoading ? 'Logging in...' : 'Log In'}
          </button>
        </form>

        <div className="login-footer">
          <p className="login-text">
            Don't have an account?{' '}
            <Link to="/register" className="login-link">Register</Link>
          </p>
          <Link to="/forgot-password" className="forgot-password">
            Forgot password?
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
